<?php/** * 行为型模式 * * 中介模式 对个对象之间互相关联，成网状关系，为了降低耦合度，让这些对象通过一个中介对象交互 *///这是不使用中介模式的实现方式class Player {    public $money = 100;    public function win(Player $player, int $money) {        //输钱的人        $player->money -= $money;        //自己的余额增加        $this->money += $money;    }}//4一个玩家一起玩游戏，只有一个赢家$player1 = new Player();$player2 = new Player();$player3 = new Player();$player4 = new Player();$player1->win($player2, 10);$player1->win($player3, 8);$player1->win($player4, 20);echo "1号玩家的余额：".$player1->money.PHP_EOL;echo "2号玩家的余额：".$player2->money.PHP_EOL;echo "3号玩家的余额：".$player3->money.PHP_EOL;echo "4号玩家的余额：".$player4->money.PHP_EOL;