<?phpnamespace Helper;class Client{    public static function curl($uri, $data = FALSE, $timeOut = 30, $is_post = 0)    {        $ch = curl_init();        curl_setopt($ch, CURLOPT_POST, $is_post);        curl_setopt($ch, CURLOPT_URL, $uri);        if(false != $data){            curl_setopt($ch, CURLOPT_POSTFIELDS, json_encode($data));        }        curl_setopt($ch, CURLOPT_TIMEOUT, $timeOut);        curl_setopt($ch, CURLOPT_SSL_VERIFYPEER, 0);        curl_setopt($ch, CURLOPT_SSL_VERIFYHOST, 0);        curl_setopt($ch, CURLOPT_USERAGENT, 'Mozilla/4.0 (compatible; MSIE 6.0; Windows NT 5.0)');        curl_setopt($ch, CURLOPT_RETURNTRANSFER, true);        $r = curl_exec($ch);        $result = json_decode ($r ,true);        if($result){            if($result['status'])            {                return $result['data'];            }            else            {                //LastError::set($result['code'],$result['message']);                return false;            }        }else{            return $r;        }    }    public static function muilti(array $urls)    {        $chs = [];        foreach ($urls as $url) {            $chs[] = $ch = curl_init($url);            curl_setopt($ch, CURLOPT_RETURNTRANSFER, true);        }        $mh = curl_multi_init();        foreach ($chs as $ch) {            curl_multi_add_handle($mh, $ch);        }        //执行批处理等待全部完成        $running = null;        do {            curl_multi_exec($mh, $running);        } while ($running);        foreach ($chs as $ch) {            echo $resp = curl_multi_getcontent($ch);        }        curl_multi_close($mh);    }}