<?php/** * Created by PhpStorm. * User: chentao * Date: 2020/1/12 * Time: 4:00 PM */$client = new swoole\Client(SWOOLE_SOCK_TCP);//连接到服务器if (!$client->connect('127.0.0.1', 9501, 0.5)){    die("connect failed.");}//向服务器发送数据if (!$client->send("hello world")){    die("send failed.");}//从服务器接收数据$data = $client->recv();if (!$data){    die("recv failed.");}echo $data;//关闭连接$client->close();