<?php/** * 新增定义常量数组的方式 * * Created by PhpStorm. * User: chentao * Date: 2020/1/15 * Time: 3:28 PM *///php5.6const Mysql = ['host'=>'127.0.0.1','port'=>80];//php7define('Redis',['host'=>'127.0.0.1','port'=>6379]);print_r(Mysql);print_r(Redis);print_r(Test::Shanghai);//const 与 define的区别//都是用来定义常量的//区别在于/** * 1. const 是语言结构，define是函数，语言结构比函数快 * 2. const 可以用来定义成员常量， define不能用于定义类的成员变量，只能定义全局常量 * 3. const 可以在类中使用， define不能 * 4. const 不能用条件语句定义常量 */class Test{    const Shanghai = ['city'=>1,'num'=>45];}